	/** 生成xml格式 海关商品报备(订单报文、商品报备报文)对接专用 */
	function arrayToXml($arr, $funcy){

		$xml = "";
		
		foreach ($arr as $key=>$val){
			if(is_numeric($key)){
				$xml .= "<".$funcy.">".arrayToXml($val, $funcy)."</".$funcy.">";
			}else{
				if(is_array($val)){
					$xml.="<".$key.">".arrayToXml($val, $funcy)."</".$key.">";
				}else{
					$xml.="<".$key.">".$val."</".$key.">";
				}
			}
		}

		return $xml;
	}

