	// 错误代码
	function error_msg($str){
        if(preg_match("/^0.1./", $str)){
            $msg = '参数缺少';
        }else if(preg_match("/^0.2./", $str)){
        	$msg = '参数错误';
        }else if(preg_match("/^0.3./", $str)){
        	$msg = '安全错误';
        }else if(preg_match("/^0.4./", $str)){
        	$msg = '格式错误';
        }else if(preg_match("/^0.5./", $str)){
        	$msg = '签名错误';
        }else if(preg_match("/^0.6./", $str)){
        	$msg = '解析错误';
        }else if(preg_match("/^0.7./", $str)){
        	$msg = '同步错误';
        }else if(preg_match("/^0.8./", $str)){
        	$msg = '解码错误';
        }else if(preg_match("/^1.1./", $str)){
        	$msg = '格式错误';
        }else if(preg_match("/^1.2./", $str)){
        	$msg = '解析错误';
        }else if(preg_match("/^1.3./", $str)){
        	$msg = '签名错误';
        }else if(preg_match("/^1.4./", $str)){
        	$msg = '接收接收人错误';
        }else if(preg_match("/^1.5./", $str)){
        	$msg = '报文入库错误';
        }else if(preg_match("/^1.6./", $str)){
        	$msg = '处理报文接收回执';
        }else if(preg_match("/^1.7./", $str)){
        	$msg = '处理报文审核回执';
        }else if(preg_match("/^-999/", $str)){
        	$msg = '报文类型不接收';
        }else if(preg_match("/^unknown/", $str)){
        	$msg = '系统错误';
        }else{
        	$msg = $str;
        }

        return $msg;
	}